insert.foreach:
INSERT ${table.dbName}
(
<#list table.columns as column>
${column.dbName}<#if column.end>,</#if>
</#list>
)
VALUES
(
<#list table.columns as column>
item.${column.name}<#if column.end>,</#if>
</#list>
);

mapper.update:
UPDATE ${table.dbName}

<#list table.columns as column>
${column.dbName} = '#'{${column.name}}
</#list>

WHERE id = '#'{id}

update.foreach:
UPDATE ${table.dbName}

<#list table.columns as column>
${column.dbName} = '#'{item.${column.name}}
</#list>

WHERE id = '#'{id}
